pragma: str = "#pragma once\n\n"

left_bracket: str = "{"
right_bracket: str = "}"


def indent(i: int) -> str:
    return ' ' * (i * 4)


def needs_setter(type_: enums.CppType) -> bool:
    return type_ in [enums.CppType.USIZE, enums.CppType.STRING, enums.CppType.BOOL]


def needs_quotes(type_: enums.CppType) -> bool:
    return type_ in [enums.CppType.STRING, enums.CppType.STRING_STATIC_CONST]
